package Lab22_07;

//4.Write a program to calculate the sum of first 10 natural number.

public class NaturalNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         int n=10;
         int i=1;
         int sum=0;
         
        while(i<=n){
        	 sum=sum+i;
        	 i++;
        	 
         }
         System.out.println(sum);
	}

}
