package WrapperClass;

public class AutoboxingEg {

	// it converts primitive data types to objects
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 5;
		Integer b = a;
		
		
		System.out.println(b);

		
		// unboxing
		
		Integer d = 10;
		int e = d;
		
		System.out.println(e);

		


	}

}
